base64_str = 'data:image/jpeg;base64,'

100.times do |n|
  Merchandise.seed do |s|
    s.id = n + 1
    s.title = Faker::Lorem.word
    s.description = Faker::Lorem.paragraph(sentence_count: 2, supplemental: false, random_sentences_to_add: 4)
    s.price = rand(0..10_000)
    s.seller_id = rand(1..10)
    s.public_status = 0
    s.condition = 1
  end

  department = Department.all.sample
  DividedDepartment.seed do |dd|
    dd.department_id = department.id
    dd.merchandise_id = n + 1
  end
end
